import React, { useState, useEffect, useRef } from "react";
import { DropdownProps } from "./Dropdown.types";
import { Icon } from "../Icon";
import { DropdownContainer, DropdownHeader, DropdownListContainer, DropdownList, ListItem, ListItemText, ListItemIcon, DropdownTitle, DropdownTitleContainer, DropdownSelectedText } from "./Dropdown.styled";

export const Dropdown = ({
  options,
  placeholder = "Select...",
  label,
  onChange,
  selectedOption,
  isDisabled = false,
  isMultiSelect = false,
  className,
  testId = "dropdown",
}: DropdownProps) => {
  const [isOpen, setIsOpen] = useState(false);
  const [selected, setSelected] = useState<string[]>([]);
  const dropdownRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (selectedOption) {
      if (Array.isArray(selectedOption)) {
        setSelected(selectedOption);
      } else {
        setSelected([selectedOption]);
      }
    } else {
      setSelected([]);
    }
  }, [selectedOption]);

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setIsOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  const toggleDropdown = () => {
    if (!isDisabled) {
      setIsOpen(!isOpen);
    }
  };

  const handleOptionClick = (option: string) => {
    let newSelected: string[];

    if (isMultiSelect) {
      if (selected.includes(option)) {
        newSelected = selected.filter(item => item !== option);
      } else {
        newSelected = [...selected, option];
      }
    } else {
      newSelected = [option];
      setIsOpen(false);
    }

    setSelected(newSelected);
    onChange?.(isMultiSelect ? newSelected : option);
  };

  const getDisplayText = () => {
    if (selected.length === 0) return placeholder;
    return selected.join(", ");
  };

  return (
    <DropdownContainer className={className} ref={dropdownRef} data-testid={testId}>
      {label && (
        <DropdownTitleContainer>
          <DropdownTitle>{label}</DropdownTitle>
        </DropdownTitleContainer>
      )}
      <DropdownHeader onClick={toggleDropdown} isDisabled={isDisabled} isOpen={isOpen} data-testid={`${testId}-header`}>
        <DropdownSelectedText isPlaceholder={selected.length === 0}>{getDisplayText()}</DropdownSelectedText>
        <Icon name={isOpen ? "chevron-up" : "chevron-down"} size={16} />
      </DropdownHeader>
      {isOpen && (
        <DropdownListContainer data-testid={`${testId}-list-container`}>
          <DropdownList>
            {options.map((option, index) => (
              <ListItem
                key={index}
                onClick={() => handleOptionClick(option)}
                isSelected={selected.includes(option)}
                data-testid={`${testId}-option-${index}`}
              >
                <ListItemText>{option}</ListItemText>
                {selected.includes(option) && (
                  <ListItemIcon>
                    <Icon name="check" size={16} />
                  </ListItemIcon>
                )}
              </ListItem>
            ))}
          </DropdownList>
        </DropdownListContainer>
      )}
    </DropdownContainer>
  );
};

export default Dropdown;
